// src/pages/HomePage.js
import React, { useEffect, useRef } from 'react';
import './Hometest.css'; // Import the CSS for this component

/**
 * HomePage component
 * This component renders a hero section with a sticky sliding tab navigation and several content sections.
 */
const Hometest = () => {
  // useRef to reference the tab container element
  const tabContainerRef = useRef(null);
  // useRef to reference the sliding indicator element
  const tabSliderRef = useRef(null);
  // useRef to hold an array of tab elements (we assign these via the ref attribute)
  const tabRefs = useRef([]);
  // This value is the height of the tab container (70px as defined in the CSS)
  const tabContainerHeight = 70;
  // useRef to store the current active tab id
  const currentIdRef = useRef(null);

  // Define our tabs as an array of objects (href is the id of the target section)
  const tabs = [
    { href: '#tab-es6', label: 'ES6' },
    { href: '#tab-flexbox', label: 'Flexbox' },
    { href: '#tab-react', label: 'React' },
    { href: '#tab-angular', label: 'Angular' },
    { href: '#tab-other', label: 'Other' },
  ];

  /**
   * useEffect to add event listeners for scroll and resize.
   * These events recalcualte the sliderâ€™s position and the sticky state of the tab container.
   */
  useEffect(() => {
    const onScroll = () => {
      checkTabContainerPosition();
      findCurrentTabSelector();
    };

    const onResize = () => {
      if (currentIdRef.current) {
        // If a tab is active, update the slider's CSS
        setSliderCss();
      }
    };

    // Add scroll and resize event listeners
    window.addEventListener('scroll', onScroll);
    window.addEventListener('resize', onResize);

    // Clean up the event listeners when component unmounts
    return () => {
      window.removeEventListener('scroll', onScroll);
      window.removeEventListener('resize', onResize);
    };
  }, []);

  /**
   * Checks the tab container's position relative to the window scroll.
   * If the page has scrolled past the hero section, the tab container gets a fixed position.
   */
  const checkTabContainerPosition = () => {
    // Get the hero section element by its class name
    const etHeroTabs = document.querySelector('.et-hero-tabs');
    if (!etHeroTabs || !tabContainerRef.current) return;
    // Calculate the offset: top of hero section + its height - tab container height
    const offset = etHeroTabs.getBoundingClientRect().top + etHeroTabs.offsetHeight - tabContainerHeight;
    // If we have scrolled past the offset, add the fixed-top class; otherwise, remove it
    if (window.pageYOffset > offset) {
      tabContainerRef.current.classList.add('et-hero-tabs-container--top');
    } else {
      tabContainerRef.current.classList.remove('et-hero-tabs-container--top');
    }
  };

  /**
   * Iterates over each tab element and checks which section is currently in view.
   * Updates the current active tab and calls setSliderCss() to update the slider indicator.
   */
  const findCurrentTabSelector = () => {
    let newCurrentId = null;
    let newCurrentTab = null;
    // Iterate over each tab element in tabRefs
    tabRefs.current.forEach((tab) => {
      const id = tab.getAttribute('href');
      if (!id) return;
      const section = document.querySelector(id);
      if (!section) return;
      // Calculate the top and bottom offsets for the section
      const offsetTop = section.offsetTop - tabContainerHeight;
      const offsetBottom = section.offsetTop + section.offsetHeight - tabContainerHeight;
      // If the window's scroll position is within the section's boundaries, mark this tab as current
      if (window.pageYOffset > offsetTop && window.pageYOffset < offsetBottom) {
        newCurrentId = id;
        newCurrentTab = tab;
      }
    });
    // If the active tab has changed, update the reference and update the slider CSS
    if (currentIdRef.current !== newCurrentId || currentIdRef.current === null) {
      currentIdRef.current = newCurrentId;
      setSliderCss(newCurrentTab);
    }
  };

  /**
   * Sets the slider indicator's width and left position based on the current tab element.
   * @param {HTMLElement} currentTabElement - The currently active tab element.
   */
  const setSliderCss = (currentTabElement) => {
    if (!tabSliderRef.current || !currentTabElement) return;
    // Get the width of the active tab
    const width = currentTabElement.offsetWidth;
    // Get the left position relative to the viewport
    const left = currentTabElement.getBoundingClientRect().left;
    // Update the slider's style to match the active tab
    tabSliderRef.current.style.width = `${width}px`;
    tabSliderRef.current.style.left = `${left}px`;
  };

  /**
   * Handles the click on a tab.
   * Prevents the default action and smoothly scrolls to the corresponding section.
   * @param {Event} event - The click event.
   * @param {string} href - The target section's id (e.g., "#tab-es6").
   */
  const onTabClick = (event, href) => {
    event.preventDefault();
    const targetSection = document.querySelector(href);
    if (targetSection) {
      // Use smooth scrolling to move to the target section,
      // accounting for the tab container's height so that the section title is not hidden
      window.scrollTo({
        top: targetSection.offsetTop - tabContainerHeight + 1,
        behavior: 'smooth',
      });
    }
  };

  return (
    <>
      {/* Hero Section: Contains the main heading, subheading, and tab navigation */}
      <section className="et-hero-tabs">
        <h1>STICKY SLIDER NAV</h1>
        <h3>Sliding content with sticky tab nav</h3>
        {/* Tab navigation container */}
        <div className="et-hero-tabs-container" ref={tabContainerRef}>
          {tabs.map((tab, index) => (
            <a
              key={index}
              className="et-hero-tab"
              href={tab.href}
              onClick={(e) => onTabClick(e, tab.href)}
              // Save a reference to each tab element so we can measure its dimensions
              ref={(el) => (tabRefs.current[index] = el)}
            >
              {tab.label}
            </a>
          ))}
          {/* Slider indicator: Its position and width are updated to match the active tab */}
          <span className="et-hero-tab-slider" ref={tabSliderRef}></span>
        </div>
      </section>

      {/* Main Content: Each section corresponds to one of the tabs */}
      <main className="et-main">
        <section className="et-slide" id="tab-es6">
          <h1>ES6</h1>
          <h3>something about es6</h3>
        </section>
        <section className="et-slide" id="tab-flexbox">
          <h1>Flexbox</h1>
          <h3>something about flexbox</h3>
        </section>
        <section className="et-slide" id="tab-react">
          <h1>React</h1>
          <h3>something about react</h3>
        </section>
        <section className="et-slide" id="tab-angular">
          <h1>Angular</h1>
          <h3>something about angular</h3>
        </section>
        <section className="et-slide" id="tab-other">
          <h1>Other</h1>
          <h3>something about other</h3>
        </section>
      </main>
    </>
  );
};

export default Hometest;
