import React, { useEffect } from 'react';
import './Footer.css';

function Footer() {
  useEffect(() => {
    const loadTypeform = () => {
      // Ensure the Typeform script is loaded only once
      if (!document.querySelector('script[src="https://embed.typeform.com/embed.js"]')) {
        const script = document.createElement('script');
        script.src = "https://embed.typeform.com/embed.js";
        script.async = true;
        document.body.appendChild(script);
      } else {
        // If script exists, reload the widget
        window.Typeform?.refresh();
      }
    };

    loadTypeform();

    // Refresh Typeform when user navigates back
    const handleVisibilityChange = () => {
      if (document.visibilityState === "visible") {
        window.Typeform?.refresh();
      }
    };

    document.addEventListener("visibilitychange", handleVisibilityChange);

    return () => {
      document.removeEventListener("visibilitychange", handleVisibilityChange);
    };
  }, []);

  return (
    <footer className="footer">
      <h3>Stay Updated</h3>
      <p>Enter your email to get the latest updates:</p>
      <div 
        className="typeform-widget" 
        data-url="https://form.typeform.com/to/RZQ68gm8"
        style={{ width: '75%', height: '200px', margin: '0 auto' }}
      ></div>
    </footer>
  );
}

export default Footer;
