// src/pages/Hometest.js
import React, { useEffect, useRef } from 'react';
import { Link } from 'react-router-dom'; // Import Link for routing
import './Hometest.css';

const Hometest = () => {
  // References for sticky nav functionality
  const tabContainerRef = useRef(null);
  const tabSliderRef = useRef(null);
  const tabRefs = useRef([]);
  const tabContainerHeight = 70;
  const currentIdRef = useRef(null);

  // Define the tab links for the sticky nav
  const tabs = [
    { href: '#tab-es6', label: 'ES6' },
    { href: '#tab-flexbox', label: 'Flexbox' },
    { href: '#tab-react', label: 'React' },
    { href: '#tab-angular', label: 'Angular' },
    { href: '#tab-other', label: 'Other' },
  ];

  // New function to update the nav's top position smoothly
  const updateNavPosition = () => {
    if (!tabContainerRef.current) return;
    // Calculate the initial top position (bottom of hero)
    const initialTop = window.innerHeight - tabContainerHeight;
    // Subtract the scroll value, but do not go below 0
    const newTop = Math.max(0, initialTop - window.scrollY);
    tabContainerRef.current.style.top = `${newTop}px`;
  };

  // Set up scroll and resize event listeners
  useEffect(() => {
    const onScroll = () => {
      updateNavPosition();
      findCurrentTabSelector();
    };

    const onResize = () => {
      if (currentIdRef.current) {
        setSliderCss();
      }
      updateNavPosition();
    };

    window.addEventListener('scroll', onScroll);
    window.addEventListener('resize', onResize);

    // Run once on mount
    updateNavPosition();

    return () => {
      window.removeEventListener('scroll', onScroll);
      window.removeEventListener('resize', onResize);
    };
  }, []);

  // Smooth-scroll back to the top (used elsewhere if needed)
  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: 'smooth',
    });
  };

  // When a tab is clicked, smooth-scroll to the corresponding section
  const onTabClick = (event, href) => {
    event.preventDefault();
    const targetSection = document.querySelector(href);
    if (targetSection) {
      window.scrollTo({
        top: targetSection.offsetTop - tabContainerHeight + 1,
        behavior: 'smooth',
      });
    }
  };

  // Determine which tab is active based on scroll position
  const findCurrentTabSelector = () => {
    let newCurrentId = null;
    let newCurrentTab = null;
    tabRefs.current.forEach((tab) => {
      const id = tab.getAttribute('href');
      if (!id) return;
      const section = document.querySelector(id);
      if (!section) return;
      const offsetTop = section.offsetTop - tabContainerHeight;
      const offsetBottom = section.offsetTop + section.offsetHeight - tabContainerHeight;
      if (window.pageYOffset > offsetTop && window.pageYOffset < offsetBottom) {
        newCurrentId = id;
        newCurrentTab = tab;
      }
    });
    if (currentIdRef.current !== newCurrentId || currentIdRef.current === null) {
      currentIdRef.current = newCurrentId;
      setSliderCss(newCurrentTab);
    }
  };

  // Update the blue slider indicator to match the active tab's dimensions
  const setSliderCss = (currentTabElement) => {
    if (!tabSliderRef.current || !currentTabElement) return;
    const width = currentTabElement.offsetWidth;
    const left = currentTabElement.getBoundingClientRect().left;
    tabSliderRef.current.style.width = `${width}px`;
    tabSliderRef.current.style.left = `${left}px`;
  };

  return (
    <>
      {/* Hero section with sticky slider navigation */}
      <section className="et-hero-tabs">
        <h1>STICKY SLIDER NAV</h1>
        <h3>Sliding content with sticky tab nav</h3>
        <div className="et-hero-tabs-container" ref={tabContainerRef}>
          {tabs.map((tab, index) => (
            <a
              key={index}
              className="et-hero-tab"
              href={tab.href}
              onClick={(e) => onTabClick(e, tab.href)}
              ref={(el) => (tabRefs.current[index] = el)}
            >
              {tab.label}
            </a>
          ))}
          <span className="et-hero-tab-slider" ref={tabSliderRef}></span>
        </div>
      </section>

      {/* Main content sections with individual background images and a thick rounded border */}
      <main className="et-main">
        {/* ES6 tab: includes the "The Idea" button */}
        <section className="et-slide" id="tab-es6">
          <div className="slide-background slide-bg-es6">
            <h1>ES6</h1>
            <h3>Information about ES6 here</h3>
            {/* "Learn More" button linking to TheIdea page */}
            <div className="learn-more">
              <Link to="/theidea" className="learn-more-button">
                The Idea
              </Link>
            </div>
          </div>
        </section>

        {/* Flexbox tab: includes the "The Why" button */}
        <section className="et-slide" id="tab-flexbox">
          <div className="slide-background slide-bg-flexbox">
            <h1>Flexbox</h1>
            <h3>Information about Flexbox here</h3>
            {/* "Learn More" button linking to TheWhy page */}
            <div className="learn-more">
              <Link to="/thewhy" className="learn-more-button">
                The Why
              </Link>
            </div>
          </div>
        </section>

        {/* React tab: includes the "The Creator" button */}
        <section className="et-slide" id="tab-react">
          <div className="slide-background slide-bg-react">
            <h1>React</h1>
            <h3>Information about React here</h3>
            {/* "Learn More" button linking to TheCreator page */}
            <div className="learn-more">
              <Link to="/thecreator" className="learn-more-button">
                The Creator
              </Link>
            </div>
          </div>
        </section>

        {/* Optional additional sections */}
        <section className="et-slide" id="tab-angular">
          <div className="slide-background slide-bg-angular">
            <h1>Angular</h1>
            <h3>Information about Angular here</h3>
          </div>
        </section>
        <section className="et-slide" id="tab-other">
          <div className="slide-background slide-bg-other">
            <h1>Other</h1>
            <h3>Information about Other here</h3>
          </div>
        </section>
      </main>
    </>
  );
};

export default Hometest;
